use std::collections::HashSet;

pub fn run() {
    let input = "";
    solution(input, 4);
    solution(input, 14)
}

fn solution(input: &str, n: usize) {
    let chars = input.clone().chars().collect::<Vec<char>>();
    for i in 0..chars.len() {
        let offset = i + n;
        if offset > chars.len() {
            break;
        }
        let chunk = &chars[i..offset];
        let set: HashSet<&char> = chunk.iter().collect::<HashSet<&char>>();
        if set.len() == chunk.len() {
            println!("{:?}", offset);
            break;
        }
    }
}
